
public class pattern {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=0;i<4;i++)
{
	for(int j=4-i;j>=0;j--)
	{
		System.out.println(" ");
	}
	for( int k=4;k==k+i;k++)
	{
		System.out.println("*");
	}
	System.out.println("\n");
}
	}

}
